try {
  // Extract phone number from action result
  const phoneNumber = inputs.actions?.['ef4212aa_0fbe_4e40_a6d0_ea00bbd66d63']?.['crmto_delivery_phone_number'];

  // Validate phone number format
  if (!phoneNumber || typeof phoneNumber !== 'string') {
    return { isValid: false, error: 'Phone number is required' };
  }

  const phoneRegex = /^\d{3}-\d{3}-\d{4}$/;
  const isValid = phoneRegex.test(phoneNumber);

  return {
    isValid,
    phoneNumber,
    error: isValid ? null : 'Invalid phone number format (expected: XXX-XXX-XXXX)'
  };
} catch (error) {
  return { isValid: false, error: error.message };
}
